    fun findSubstring(s: String, words: Array<String>): List<Int> {
        val wordSize = words[0].length
        val window = wordSize * words.size

        val list = mutableListOf<Int>()
        val expected = words.groupingBy { it }.eachCount()
        val count = words.associateWith { 0 }.toMutableMap()

        for (i in 0..<s.length - window + 1) {
            for (j in words.indices) {
                val slice = s.slice(i + j * wordSize..<i + (j + 1) * wordSize)
                if (slice !in words) {
                    break
                }
                count[slice] = (count[slice] ?: 0) + 1
            }

            if (count eachCountEquals expected) {
                list.add(i)
            }

            for ((k) in count) {
                count[k] = 0
            }
        }

        return list
    }

    private infix fun <K, V> Map<K, V>.eachCountEquals(other: Map<K, V>) = !any { (k, v) -> v != other[k] }

